pragma solidity ^0.4.0;
pragma experimental ABIEncoderV2;

contract Activities {
// Contract to Add and Edit Physiotherapy Activities
    uint constant n = 15; // n - to define the number of workers
    address[n] public workers; // workers - array of 'n' addresses for workers
    struct ultra_sound{
        uint power;
        uint freq;
    }
    struct currents{
        uint diam;
        uint humid;
    }
    struct aerosols{
        uint qt;
        uint vent_level;
    }
    struct activity_type{
        ultra_sound us;
        currents c;
        aerosols ae;
    }
    struct activity { // Activity Struct
        uint dateBegin;
        uint dateEnd;
        string description;
        activity_type at;
    }
    // Add an Activity
    function activitiesAdd(uint256 workerId, activity a) public returns (uint256, activity){
        require(workerId >= 0 && workerId <= n);

        workers[workerId] = msg.sender;

        return (workerId, a);
    }
    // Edit an Activity
    function activitiesEdit(uint256 workerId, activity a) public returns (uint256, activity){
        require(workerId >= 0 && workerId <= n);

        workers[workerId] = msg.sender;

        return (workerId, a);
    }
}
